(1718, 174, '_data-visualization-title', 'field_5e99b0604a6a4'),
(1719, 174, 'data-visualization-description', 'Visualizing your data can help you quickly understand complex patterns in data sets as well as help your clients use your product or service in a more easy way. Our team will create multi-functional and interactive visualizations that would make the users want to come back to them “just to play a little more”.'),
(1720, 174, '_data-visualization-description', 'field_5e99b0794a6a5'),
(1721, 174, 'web-application-development-title', 'Web Application Development'),
(1722, 174, '_web-application-development-title', 'field_5e99b0ae4a6a7'),
